import React, { Component } from "react";
import { MenuItems } from "./MenuItems";
import { Button } from "../Button";
import "./Navbar.css";
import { NavLink } from "./NavbarElements";
class Navbar extends Component {
  state = { clicked: false };

  handleClick = () => {
    this.setState({ clicked: !this.state.clicked });
  };

  render() {
    return (
      <nav className="NavbarItems">
        <h1 className="navbar-logo">
          JustRunwithIt <i className="fab fa-react "></i>
        </h1>
        <div className="menu-icon" onClick={this.handleClick}>
          <i
            className={this.state.clicked ? "fas fa-times" : "fas fa-bars"}
          ></i>
        </div>
        <ul className={this.state.clicked ? "nav-menu active" : "nav-menu"}>
          <NavLink to="/monday" activeStyle></NavLink>
          <NavLink to="/tuesday" activeStyle></NavLink>
          <NavLink to="/wednesday" activeStyle>
            Wedneday
          </NavLink>
          <NavLink to="/thursday" activeStyle>
            Thursday
          </NavLink>
          <NavLink to="/friday" activeStyle>
            Friday
          </NavLink>
          <NavLink to="/saturday" activeStyle>
            Saturday
          </NavLink>
          <NavLink to="/sunday" activeStyle>
            Sunday
          </NavLink>

          {MenuItems.map((item, index) => {
            return (
              <li key={index}>
                <a className={item.cName} href={item.url}>
                  {item.title}
                </a>
              </li>
            );
          })}
        </ul>
        <Button>Click</Button>
      </nav>
    );
  }
}

export default Navbar;
